REPLACE INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(64361, 0, 0, 'Uncle Gao, this brewery was left in your care! What have you done?', 12, 0, 100, 6, 0, 31998, 'Chen Stormstout to Player'),
(64361, 1, 0, 'At what cost? The Brewery is trashed! Infested!', 12, 0, 100, 6, 0, 29773, 'Chen Stormstout to Player'),
(64361, 2, 0, 'And there are Virmen in the main store!', 12, 0, 100, 5, 0, 29774, 'Chen Stormstout to Player'),
(64361, 3, 0, 'I think the Brewery might be on fire.', 12, 0, 100, 1, 0, 29775, 'Chen Stormstout to Player'),
(59074, 0, 0, 'Yes yes yes! So close! Just a pinch of...oops...', 14, 0, 100, 7, 0, 28290, 'Uncle Gao to Player'),
(59074, 1, 0, 'Too bitter! Just a drop of honey, and a cane of sugar, and maybe some corn? Yes, more corn!', 14, 0, 100, 6, 0, 28291,'Uncle Gao'),
(59074, 2, 0, 'Yes, yes, yes...no, no, no no no! Yes! No! Peppers!', 14, 0, 100, 5, 0, 28292, 'Uncle Gao'),
(59074, 3, 0, 'Ahh! Help! What is that thing!? It doesn''t matter, nothing will stop me now, not when I''m so close! Maybe if I don''t look at it...?', 14, 0, 100, 430, 0, 28289,'Uncle Gao'),
(59074, 4, 0, 'Is it...can it be???', 12, 0, 100, 6, 0, 28293, 'Uncle Gao to Player'),
(59074, 5, 0, 'Such harmony of flavor, such heady aroma! It is...the perfect brew!', 12, 0, 100, 0, 0, 28294,'Uncle Gao to Player'),
(59074, 6, 0, 'The name of Stormstout will be sung once again throughout the hills!', 14, 0, 100, 5, 0, 28295, 'Uncle Gao to Player'),
(59074, 7, 0, 'You again! Don''t you see? I have made a name for myself at last: I have brewed perfection!', 14, 0, 100, 6, 0, 31999, 'Uncle Gao to Player'),
(59074, 8, 0, 'Details, details.', 12, 0, 100, 274, 0, 32000,'Uncle Gao to Player'),
(59074, 9, 0, 'Look, "Chen Stormstout," we all can''t be heroes, running from our responsibilities, tromping around the Dread Wastes, saving the world. Some of us are "Artists." ', 12, 0, 100, 14, 0, 32001,'Uncle Gao to Player'),
(59074, 10, 0, 'Yes, yes. That happens.', 12, 0, 100, 1, 0, 32002,'Uncle Gao to Player'),
(59704, 0, 0, 'I am not Zan - I am Chen Stormstout!', 12, 0, 100, 5, 0, 29766,'Chen Stormstout to Player'),
(59704, 1, 0, 'Tell me, what has happened here?', 12, 0, 100, 6, 0, 29767,'Chen Stormstout to Player'),
(59704, 2, 0, 'Where are the other Stormstouts? Why are there hozen all over the brewery?', 12, 0, 100, 6, 0, 29768, 'Chen #4'),
(59704, 3, 0, 'Auntie Stormstout...why is the brewery abandoned?', 12, 0, 100, 6, 0, 29769, 'Chen #5'),
(59704, 4, 0, 'I see. And where is Uncle Gao?', 12, 0, 100, 6, 0, 29770, 'Chen #6'),
(59704, 5, 0, 'There is no time for cookies!.. Well...one cookie. Just, one.', 12, 0, 100, 5, 0, 29771, 'Chen #7'),
(59704, 6, 0, 'Wait...these are ghost cookies! These aren''t filling at all!', 12, 0, 100, 5, 0, 29772, 'Chen #8'),
(56717, 0, 0, 'Oh yeah!', 14, 0, 100, 0, 0, 0, 'Hoptallus Aggro'),
(56717, 1, 0, 'Gonna spins around!', 14, 0, 100, 0, 0, 0, 'Hoptallus Furlwind'),
(56717, 2, 0, 'Urp...eats too many carrots...', 14, 0, 100, 0, 0, 0, 'Hoptallus Carrot Breath'),
(56717, 3, 0, 'You have...turnip...for a head...', 14, 0, 100, 0, 0, 0, 'Hoptallus Death'),
(56717, 4, 0, 'Hoptallus lets out a loud screech! The Virmen are coming!', 41, 0, 100, 15, 0, 0, 'Hoptallus Screech');
