INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`,`comment`) VALUES
(56448, 0, 0, 'Water reacts violently to your contact!', 41, 0, 100, 0, 0, 0, 'Wise Mari'),
(56448, 1, 0, 'You dare to disturb these waters? You will drown!', 12, 0, 100, 0, 0, 28257, 'Wise Mari'),
(56448, 2, 0, 'Rise!', 12, 0, 100, 0, 0, 28260, 'Wise Mari'),
(56448, 2, 1, 'Spring to life!', 12, 0, 100, 0, 0, 28258, 'Wise Mari'),
(56448, 2, 2, 'Living water, bring them death!', 12, 0, 100, 0, 0, 28261, 'Wise Mari'),
(56448, 2, 3, 'Do not run, it only delays the inevitable.', 12, 0, 100, 0, 0, 28269, 'Wise Mari'),
(56448, 2, 4, 'Come in, the water\'s fine!', 12, 0, 100, 0, 0, 28270, 'Wise Mari'),
(56448, 2, 5, 'Let death wash over you!', 12, 0, 100, 0, 0, 28271, 'Wise Mari'),
(56448, 3, 0, 'Behold the POWER of torrential waters!', 12, 0, 100, 0, 0, 28266, 'Wise Mari'),
(56448, 4, 0, '%s draws from the surrounding water!', 41, 0, 100, 0, 0, 0, 'Wise Mari to Wise Mari'),
(56448, 5, 0, 'The darkness clears...', 12, 0, 100, 0, 0, 28262, 'Wise Mari'),
(56448, 6, 0, 'I peer into these waters and my reflection is empty...', 12, 0, 100, 0, 0, 28263, 'Wise Mari'),
(56448, 7, 0, 'Waters... claim me...', 12, 0, 100, 0, 0, 28264, 'Wise Mari'),
(56448, 8, 0, 'Your cause is hopeless!', 12, 0, 100, 0, 0, 28268, 'Wise Mari'),
(56448, 8, 1, 'As the waters foretold!', 12, 0, 100, 0, 0, 28267, 'Wise Mari');
