INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(34668, 0, 0, 'Haha! That should be a blast!', 12, 0, 100, 11, 0, 0, 'Sassy Hardwrench'),
(34668, 1, 0, 'He''ll get me more later? How are we supposed to get you promoted to Trade $g Prince : Princess; without our number one product on the shelves?!!!', 12, 0, 100, 274, 0, 0, 'Sassy Hardwrench'),
(34668, 2, 0, 'Ace, Izzy, Gobber, you three make sure you tag along with $N and help $g him : her; take care of those deadbeats!', 12, 0, 100, 1, 0, 0, 'Sassy Hardwrench'),
(34668, 3, 0, 'Go get ''em, boss!', 12, 0, 100, 25, 0, 2304, 'Sassy Hardwrench'),
(34668, 4, 0, 'You better get upstairs pronto, boss. Trade Prince Galllywix is waiting for you.', 12, 0, 100, 6, 0, 0, 'Sassy Hardwrench'),
(34668, 5, 0, '$N, you have to hurry and break your moolah out of the bank!', 12, 0, 100, 5, 0, 0, 'Sassy Hardwrench'),
(34668, 6, 0, 'I can''t stand to watch. So many good memories. So much profit.', 12, 0, 100, 18, 0, 0, 'Sassy Hardwrench'),
