INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(36403, 0, 0, 'Never fear, Trade Prince Gallywix is here. We''ll be on our way to our new home in Azshara before you know it! Despite the bumbling interference of $N.', 14, 0, 100, 5, 0, 19589, 'Trade Prince Gallywix'),
