INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(39582, 0, 0, 'I SEE THE TRAITOR IS HERE TO RESCUE YOU, WARCHIEF. HOW CONVENIENT. YOU WILL BOTH BOW TO ME OR FALL TOGETHER!', 14, 0, 100, 0, 0, 19567, 'Trade Prince Gallywix'),
(39582, 1, 0, 'I''m so money!', 14, 0, 100, 0, 0, 19579, 'Trade Prince Gallywix to Thrall'),
(39582, 2, 0, 'Gotta trim some fat around here. I''m downsizing the company!', 14, 0, 100, 0, 0, 19573, 'Trade Prince Gallywix to Ultimate Footbomb Uniform'),
(39582, 1, 1, 'Here''s some of that moolah back that I took from you!', 14, 0, 100, 0, 0, 19577, 'Trade Prince Gallywix to Ultimate Footbomb Uniform'),
(39582, 3, 0, 'Here, I need to unload some toxic assets!', 14, 0, 100, 0, 0, 19568, 'Trade Prince Gallywix'),
(39582, 4, 0, 'Uncle! Uncle! I give! You guys are too much for me!', 14, 0, 100, 20, 0, 19580, 'Trade Prince Gallywix'),
(39582, 5, 0, 'I''m beaten. You''ve shown me the error of my ways. From here on out, I promise to reform the way the cartel is run!', 14, 0, 100, 274, 0, 19581, 'Trade Prince Gallywix'),
(39582, 6, 0, 'I''m your goblin, Thrall. What would you have of me?', 14, 0, 100, 6, 0, 19582, 'Trade Prince Gallywix'),
(39582, 7, 0, 'It will be as you say! Long live the Bilgewater Cartel! For the Horde!', 14, 0, 100, 6, 0, 19583, 'Trade Prince Gallywix');
